<template>  <div id="app" class="noselect">    <!--头部-->    <ui-head :title="title">      <ui-btn type="reset1" class="reset"></ui-btn>    </ui-head>    <!--模型区域-->    <div class="container">      <div class="left">        <div class="leftBody">          <div class="leftMain" :style="leftMain">            <div id="leftCanvas">            </div>          </div>        </div>      </div>      <div id="main" :style="'position:relative;'">        <div class="mainD"             :style="'border:1px dashed #ccc;position:absolute;top:0;left:0;right:0;bottom:0;margin:auto;width:430px;height:282px;'">          <p class="startC"             :style="'cursor: pointer;position:absolute;top:0;left:0;right:0;bottom:0;margin:auto;text-align:center;width:100px;height:20px;line-height:20px;'">            点击开始</p>        </div>      </div>      <div class="right">        <div class="right-top">          <div class="step">            <span></span>          </div>          <div class="step">            <span></span>          </div>          <div class="step">            <span></span>          </div>        </div>        <div :style="'height:310px;position:absolute;top:0;bottom:0;margin:auto;'">          <ui-group type="radio"                    :margin="12"                    :width="98"                    :height="44"                    :disabled="yes"                    :groups="groups"                    v-model="radio_checked1"></ui-group>          <ui-btn type="switch"                  :width="98"                  :height="96"                  :vertical="true"                  v-model="switch_checked4"                  style="margin-top: 30px">辅助线          </ui-btn>        </div>      </div>    </div>  </div></template><script>  import uiHead from '@/components/UI/uiHead';//头部  import uiBtn from '@/components/UI/uiBtn';//按钮  import uiGroup from '@/components/UI/uiGroup';//按钮  export default {    name: 'app',    components: {uiHead, uiBtn, uiGroup},    data() {      return {        title: '2013年重庆理（5）',        leftMain: {          height: '400px',          width: '100%',          background: 'url(static/UI/subject.png) no-repeat',          backgroundSize: '100% 100%',          overflow: 'hidden'        },        radio_checked1: '',        radio_checked1O: '',        switch_checked4: false,        switch_checked4O1: '',        switch_checked4O2: '',        yes: true,        start: {          color: '#000000',          marginBottom: '40px'        },        groups: [{          name: 'one',          txt: '正视'        }, {          name: 'two',          txt: '侧视'        }, {          name: 'three',          txt: '俯视'        }]      }    },    created() {        document.title = this.title;    },    mounted() {      //禁止选择      document.onselectstart = function () {        return false;      };      this.init();    },    computed: {},    methods: {      init() {        var camera1 = null;        var camera2 = null;        var scene1 = new THREE.Scene();        var scene2 = new THREE.Scene();        var renderer1 = null;        var renderer2 = null;        var controls2 = null;        var dragPlane = null;        var cubeLine = new THREE.Group();        var triangle = new THREE.Group();        var guide = new THREE.Group();        var leftLine = new THREE.Group();        var mainWidth = null;        var mainHeight = null;        var raycaster = new THREE.Raycaster();        var Plane = new THREE.Plane();        var offset = new THREE.Vector3();        var intersection = new THREE.Vector3();        var mouse = new THREE.Vector2();        var INTERSECTED = null;        var mousedownflag = false;        var mousedownflag2 = false;        var isMob = /iPad|Android/g.test(navigator.userAgent);        var a = null;        var selectobj = null;        var selectobjs = [];        var thiz = this;        var leftCanvasWidth;        var leftCanvasHeight;        var TH = {            //拖动事件            onDocumentMouseDown: function (event) {                var offsetLeft = parseInt($('#main').offset().left);                var offsetTop = parseInt($('#main').offset().top);                event.preventDefault();                var mouse = {};                mouse.x = ((event.clientX - offsetLeft) / mainWidth ) * 2 - 1;                mouse.y = -( (event.clientY - offsetTop) / mainHeight ) * 2 + 1;                raycaster.setFromCamera(mouse, camera2);                var intersects = raycaster.intersectObjects(selectobjs);                mousedownflag2=true;                if (intersects.length > 0) {                    selectobj = intersects[0].object;                    mousedownflag = true;                    controls2.enableRotate = false;                }            },            onDocumentMouseMove: function (event) {                var offsetLeft = parseInt($('#main').offset().left);                var offsetTop = parseInt($('#main').offset().top);                event.preventDefault();                var mouse = {};                mouse.x = ((event.clientX - offsetLeft) / mainWidth ) * 2 - 1;                mouse.y = -( (event.clientY - offsetTop) / mainHeight ) * 2 + 1;                raycaster.setFromCamera(mouse, camera2);                var intersects = raycaster.intersectObjects(selectobjs);                raycaster.setFromCamera(mouse, camera2);                if (intersects.length > 0) {                    if (INTERSECTED != intersects[0].object) {                        INTERSECTED = intersects[0].object;                        Plane.setFromNormalAndCoplanarPoint(camera2.getWorldDirection(Plane.normal), INTERSECTED.position);                    }                }                if (mousedownflag) {                    if (raycaster.ray.intersectPlane(Plane, intersection)) {                        var obj = intersection.sub(offset)                        TH.drawLine(obj);                    }                }                if(mousedownflag2&&!mousedownflag){                    thiz.radio_checked1='';                }            },            onDocumentMouseUp: function (event) {                event.preventDefault();                mousedownflag = false;                mousedownflag2 = false;                selectobj = null;                controls2.enableRotate = true;            },            onDocumentTouchStart: function (event) {                var offsetLeft = parseInt($('#main').offset().left);                var offsetTop = parseInt($('#main').offset().top);                event.preventDefault();                if (event.touches.length === 1) {                    var mouse = {};                    mouse.x = ((event.touches[0].pageX - offsetLeft) / mainWidth ) * 2 - 1;                    mouse.y = -( (event.touches[0].pageY - offsetTop) / mainHeight ) * 2 + 1;                    raycaster.setFromCamera(mouse, camera2);                    var intersects = raycaster.intersectObjects(selectobjs);                    mousedownflag2=true;                    if (intersects.length > 0) {                        selectobj = intersects[0].object;                        mousedownflag = true;                        controls2.enableRotate = false;                    }                }            },            onDocumentTouchMove: function (event) {                var offsetLeft = parseInt($('#main').offset().left);                var offsetTop = parseInt($('#main').offset().top);                event.preventDefault();                if (event.touches.length === 1) {                    var mouse = {};                    mouse.x = ((event.touches[0].pageX - offsetLeft) / mainWidth ) * 2 - 1;                    mouse.y = -( (event.touches[0].pageY - offsetTop) / mainHeight ) * 2 + 1;                    raycaster.setFromCamera(mouse, camera2);                    var intersects = raycaster.intersectObjects(selectobjs);                    raycaster.setFromCamera(mouse, camera2);                    if (intersects.length > 0) {                        if (INTERSECTED != intersects[0].object) {                            INTERSECTED = intersects[0].object;                            Plane.setFromNormalAndCoplanarPoint(camera2.getWorldDirection(Plane.normal), INTERSECTED.position);                        }                    }                }                if (mousedownflag) {                    if (raycaster.ray.intersectPlane(Plane, intersection)) {                        var obj = intersection.sub(offset);                        TH.drawLine(obj);                    }                }                if(mousedownflag2&&!mousedownflag){                    thiz.radio_checked1='';                }            },            onDocumentTouchEnd: function (event) {                event.preventDefault();                mousedownflag = false;                mousedownflag2 = false;                selectobj = null;                controls2.enableRotate = true;            },          //计算界面            setStyle:function() {              var zoom, zoom1;              if (window.innerHeight < 600) {                zoom1 = 0.6;              } else {                zoom1 = 1;              }              var mainHeight = $(window).height() - 120;              var mainWidth = $(window).width() - 400 * zoom1 - 160;              if (mainWidth > 430) {                zoom = 1;              } else {                zoom = mainWidth / 430;              }              $('.left').css('zoom', zoom1);              $('#main .mainD').css('zoom', zoom);              $('#main').css({                width: mainWidth + 'px',                height: mainHeight + 'px',                marginTop: '10px',              });            },            //初始化            init1:function() {              leftCanvasWidth = $('#leftCanvas').width();              leftCanvasHeight = $('#leftCanvas').height();              //canvas渲染              if (isMob) {                renderer1 = new THREE.WebGLRenderer({antialias: true, alpha: true});              } else {                renderer1 = new THREE.CanvasRenderer({antialias: true, alpha: true});              }              renderer1.setPixelRatio(window.devicePixelRatio);              renderer1.setSize(leftCanvasWidth, leftCanvasHeight);              renderer1.setClearColor(0xffffff, 0);              document.getElementById('leftCanvas').appendChild(renderer1.domElement);              scene1 = new THREE.Scene();              camera1 = new THREE.PerspectiveCamera(40, leftCanvasWidth / leftCanvasHeight, 1, 100);              camera1.position.set(0, 0, 5);            },            init2:function() {              mainWidth = $('#main').width();              mainHeight = $('#main').height();              //canvas渲染              renderer2 = new THREE.WebGLRenderer({antialias: true});              // renderer              renderer2.setPixelRatio(window.devicePixelRatio);              renderer2.setSize(mainWidth, mainHeight);              renderer2.setClearColor(0xffffff);              document.getElementById('main').appendChild(renderer2.domElement);              // camera              camera2 = new THREE.OrthographicCamera(mainWidth / -50, mainWidth / 50, mainHeight / 50, mainHeight / -50, -10, 100);              camera2.position.set(-6, 3, 9);              // controls              controls2 = new THREE.OrbitControls(camera2, renderer2.domElement);              controls2.enableDamping = true;              controls2.dampingFactor = 0.25;              controls2.enableZoom = true;              controls2.enableRotate = true;              controls2.enablePan = false;            },            createO:function() {              //辅助线              var vertices = [], line, vertices1 = [], line1, vertices2 = [], line2, vertices3 = [], line3;              selectobjs=[]              vertices.push(                new THREE.Vector3(-5, 4, -4),                new THREE.Vector3(-5, 4, 4),                new THREE.Vector3(5, 4, 4),                new THREE.Vector3(5, 4, -4),                new THREE.Vector3(-5, 4, -4),                new THREE.Vector3(-5, 0, -4),              );              line = TH.createLineMesh(vertices, '#000000', 2, 1);              vertices1.push(                new THREE.Vector3(-5, 4, 4),                new THREE.Vector3(-5, 0, 4),              );              line1 = TH.createLineMesh(vertices1, '#000000', 2, 1);              vertices2.push(                new THREE.Vector3(5, 4, 4),                new THREE.Vector3(5, 0, 4),              );              line2 = TH.createLineMesh(vertices2, '#000000', 2, 1);              vertices3.push(                new THREE.Vector3(5, 4, -4),                new THREE.Vector3(5, 0, -4),              );              line3 = TH.createLineMesh(vertices3, '#000000', 2, 1);              guide.add(line, line1, line2, line3);              guide.visible = false;              //可拖动的点              var geometry = new THREE.PlaneGeometry(1.8, 3, 1, 1);              var texture = THREE.ImageUtils.loadTexture('./static/UI/arrow.png');              // material              var material = new THREE.MeshBasicMaterial({                map: texture,                color: '#ffffff',                side: THREE.DoubleSide,                transparent:true,              });              // mesh              dragPlane = new THREE.Mesh(geometry, material);              dragPlane.position.set(-5.1, 0, 0);              dragPlane.rotation.y = Math.PI/2;//              dragPlane.visible = false;              selectobjs.push(dragPlane);              scene2.add(guide, dragPlane);              TH.createCubeLine(0, 3, 1.5);              TH.createCubeLine(0, 2, 1);              TH.createTriangle(-5, -0.01, -4, -5, -0.01, 4, 5, -0.01, -4);              TH.createTriangle(-5, -0.01, 4, 5, -0.01, 4, 5, -0.01, -4);            },            //开始按钮            btnClick:function() {              $('canvas').show();              $('.mainD').hide();              thiz.start.color = '#cccccc';              //实线              cubeLine.visible = true;              //三角面              triangle.visible = true;              //辅助线              guide.visible = true;              //拖动的面//              dragPlane.visible = true;              $('.right-top div:eq(0)').html('<span></span>确定基本图形');              $('.right-top div:first span').css('background', '#5CAEFD');              thiz.yes = false;              thiz.switch_checked4 = true;              thiz.switch_checked4O1 = 'yes';              thiz.switch_checked4O2 = 'yes';              TH.ImgL('static/UI/subject1.png', function () {                $('.leftMain').css({                  background: 'url(static/UI/subject1.png)',                  backgroundRepeat: 'no-repeat',                  height: '400px',                  width: '100%',                  backgroundSize: '100% 100%',                  overflow: 'hidden'                });              });            },            animate:function() {              requestAnimationFrame(TH.animate);              renderer1.clear();              //面和实线场景              renderer1.render(scene1, camera1);              controls2.update();              renderer2.clear();              //面和实线场景              renderer2.render(scene2, camera2);              if (thiz.radio_checked1 === thiz.radio_checked1O) {                //不做任何操作              } else if (thiz.radio_checked1 === 'one') {                clearInterval(a);                TH.rotate1([0, 0, 18]);              } else if (thiz.radio_checked1 === 'two') {                clearInterval(a);                TH.rotate1([-12, 0, 0]);              } else if (thiz.radio_checked1 === 'three') {                clearInterval(a);                TH.rotate1([0, 16, 0.01]);              }              if (thiz.switch_checked4) {                thiz.switch_checked4O1 = 'yes';              } else {                thiz.switch_checked4O1 = '';              }              if (thiz.switch_checked4O1 == thiz.switch_checked4O2) {                //不做任何操作              } else if (thiz.switch_checked4O1 == 'yes') {                guide.visible = true;              } else if (thiz.switch_checked4O1 == '') {                guide.visible = false;              }              thiz.radio_checked1O = thiz.radio_checked1;              thiz.switch_checked4O2 = thiz.switch_checked4O1;            },            drawLine:function(obj) {              var y, h, y1;              y = obj.y.toFixed(1);              y1 = y;              h = y * 27 / 200 + 0.32;              scene2.remove(cubeLine, triangle);              scene1.remove(leftLine);              cubeLine = new THREE.Group();              triangle = new THREE.Group();              leftLine = new THREE.Group();              if (y <= 0) {                $('.right-top div').html('<span></span>');                $('.right-top div:eq(0)').html('<span></span>确定基本图形');                $('.right-top div:nth-child(1) span').css('background', '#5CAEFD');                dragPlane.position.set(-5.1, 0, 0);                TH.createCubeLine(0, 3, 1.5);                TH.createCubeLine(0, 2, 1);                TH.createTriangle(-5+0.01, -0.01, -4+0.01, -5+0.01, -0.01, 4-0.01, 5-0.01, -0.01, -4+0.01);                TH.createTriangle(-5+0.01, -0.01, 4-0.01, 5-0.01, -0.01, 4-0.01, 5-0.01, -0.01, -4+0.01);                return;              } else if (y >= 4) {                $('.right-top div').html('<span></span>');                $('.right-top div:eq(2)').html('<span></span>正侧符合还原');                $('.right-top div:nth-child(3) span').css('background', '#5CAEFD');                dragPlane.position.set(-5.1, 4, 0);                TH.createCubeLine(4, 2, 1);                TH.createCubeLine(4, 3, 1.5);                TH.ImgL('static/UI/subject2.png', function () {                  $('.leftMain').css({                    background: 'url(static/UI/subject2.png)',                    backgroundRepeat: 'no-repeat',                    height: '400px',                    width: '100%',                    backgroundSize: '100% 100%',                    overflow: 'hidden'                  });                });                y1 = 4;              } else {                $('.right-top div').html('<span></span>');                $('.right-top div:eq(1)').html('<span></span>俯视确定基调');                $('.right-top div:nth-child(2) span').css('background', '#5CAEFD');                dragPlane.position.set(-5.1, y, 0);                TH.createCubeLine(y, 2, 1);                TH.createCubeLine(y, 3, 1.5);                //左边的线                TH.createHarmoniaLine(h);                TH.ImgL('static/UI/subject1.png', function () {                  $('.leftMain').css({                    background: 'url(static/UI/subject1.png)',                    backgroundRepeat: 'no-repeat',                    height: '400px',                    width: '100%',                    backgroundSize: '100% 100%',                    overflow: 'hidden'                  });                });              }              //面              TH.createTriangle(-5+0.01, 0.01, -4+0.01, -5+0.01, 0.01, 4-0.01, 5-0.01, 0.01, -4+0.01);              TH.createTriangle(-5+0.01, 0.01, 4-0.01, 5-0.01, 0.01, 4-0.01, 5-0.01, 0.01, -4+0.01);              TH.createTriangle(-5+0.01, y1-0.01, 1-0.01, 5-0.01, y1-0.01, 1-0.01, -5+0.01, 0.01, 4-0.01);              TH.createTriangle(-5+0.01, 0.01, 4-0.01, 5-0.01, 0.01, 4-0.01, 5-0.01, y1-0.01, 1-0.01);              TH.createTriangle(-5+0.01, y1-0.01, -1+0.01, 5-0.01, y1, -1+0.01, -5+0.01, 0.01, -4+0.01);              TH.createTriangle(-5+0.01, 0.01, -4+0.01, 5-0.01, 0.01, -4+0.01, 5-0.01, y1-0.01, -1+0.01);              TH.createTriangle(-5+0.01, y1-0.01, -1+0.01, -5+0.01, y1-0.01, 1-0.01, 5-0.01, y1-0.01, -1+0.01);              TH.createTriangle(5-0.01, y1-0.01, -1+0.01, 5-0.01, y1-0.01, 1-0.01, -5+0.01, y1-0.01, 1-0.01);              TH.createTriangle(-5+0.01, y1-0.01, -1+0.01, -5+0.01, 0.01, -4+0.01, -5+0.01, y1-0.01, 1-0.01);              TH.createTriangle(-5+0.01, y1-0.01, 1-0.01, -5+0.01, 0.01, -4+0.01, -5+0.01, 0.01, 4-0.01);              TH.createTriangle(5-0.01, y1-0.01, -1+0.01, 5-0.01, 0.01, -4+0.01, 5-0.01, y1-0.01, 1-0.01);              TH.createTriangle(5-0.01, y1-0.01, 1-0.01, 5-0.01, 0.01, -4+0.01, 5-0.01, 0.01, 4-0.01);              scene2.add(triangle);            },            rotate1: function (aim) {              var position = camera2.position;              var x = aim[0] - position.x, y = aim[1] - position.y, z = aim[2] - position.z;              var n = 20, v1 = x / n, v2 = y / n, v3 = z / n;              a = setInterval(() => {                n--;                if (n < 0) {                  clearInterval(a);                  return false;                }                position = camera2.position;                camera2.position.set(position.x + v1, position.y + v2, position.z + v3);              }, 40);            },            //造线            createLineMesh:function(vertices, color, style, width) {              var lineMesh = null, geometryLine = new THREE.Geometry();              if (!color) {                color = '#000';              }              if (style == 2) {                geometryLine.vertices = vertices;                geometryLine.computeLineDistances();                lineMesh = new THREE.Line(geometryLine, new THREE.LineDashedMaterial({                  color: color,                  dashSize: 0.23,                  gapSize: 0.23,                  depthTest: false,                  transparent: true,                  linewidth: width,                  opacity: 0.5,                }));              } else if (style == 3) {                geometryLine.vertices = vertices;                lineMesh = new THREE.Line(geometryLine, new THREE.LineBasicMaterial({color: color, linewidth: width}));              }              return lineMesh;            },            //造面            createTriangle:function(x1, y1, z1, x2, y2, z2, x3, y3, z3) {              var geometry = new THREE.Geometry(); //声明一个空几何体对象              var p1 = new THREE.Vector3(x1, y1, z1); //顶点1坐标              var p2 = new THREE.Vector3(x2, y2, z2); //顶点2坐标              var p3 = new THREE.Vector3(x3, y3, z3); //顶点3坐标              geometry.vertices.push(p1, p2, p3); //顶点坐标添加到geometry对象              var face = new THREE.Face3(0, 1, 2); //创建三角面              geometry.faces.push(face); //三角面添加到几何体              var material = new THREE.MeshBasicMaterial({                color: '#A8F1E0',//三角面颜色                side: THREE.DoubleSide//两面可见              });//材质对象              var mesh = new THREE.Mesh(geometry, material);//网格模型对象              triangle.add(mesh);              scene2.add(triangle);//网格模型添加到场景中            },            //立方体方程            createCubeLine:function(h, type, width) {              var vertices = [], vertices1 = [], vertices2 = [], vertices3 = [], vertices4 = [], vertices5 = [], line,                line1, line2, line3, line4, line5, vertices6 = [], line6, vertices7 = [], line7, vertices8 = [], line8;              if (h !== 0) {                //四条边                vertices5.push(                  new THREE.Vector3(-5, h, -1),                  new THREE.Vector3(-5, 0, -4),                );                line5 = TH.createLineMesh(vertices5, '#000000', type, width);                vertices6.push(                  new THREE.Vector3(-5, h, 1),                  new THREE.Vector3(-5, 0, 4),                );                line6 = TH.createLineMesh(vertices6, '#000000', type, width);                vertices7.push(                  new THREE.Vector3(5, h, 1),                  new THREE.Vector3(5, 0, 4),                );                line7 = TH.createLineMesh(vertices7, '#000000', type, width);                vertices8.push(                  new THREE.Vector3(5, h, -1),                  new THREE.Vector3(5, 0, -4),                );                line8 = TH.createLineMesh(vertices8, '#000000', type, width);                vertices3.push(                  new THREE.Vector3(5, h, -1),                  new THREE.Vector3(5, h, 1),                );                line3 = TH.createLineMesh(vertices3, '#000000', type, width);                vertices4.push(                  new THREE.Vector3(-5, h, -1),                  new THREE.Vector3(-5, h, 1),                );                line4 = TH.createLineMesh(vertices4, '#000000', type, width);                cubeLine.add(line3, line4, line5, line6, line7, line8);              }              //底面              vertices.push(                new THREE.Vector3(-5, 0, 4),                new THREE.Vector3(-5, 0, -4),                new THREE.Vector3(5, 0, -4),                new THREE.Vector3(5, 0, 4),                new THREE.Vector3(-5, 0, 4),              );              line = TH.createLineMesh(vertices, '#000000', type, width);              if(h==0){                  h=0.01;              }              //上面              vertices1.push(                new THREE.Vector3(-5, h, 1),                new THREE.Vector3(5, h, 1),              );              line1 = TH.createLineMesh(vertices1, '#000000', type, width);              vertices2.push(                new THREE.Vector3(-5, h, -1),                new THREE.Vector3(5, h, -1),              );              line2 = TH.createLineMesh(vertices2, '#000000', type, width);              cubeLine.add(line, line1, line2);              scene2.add(cubeLine);            },            //左边异色的线            createHarmoniaLine:function(h) {              var vertices = [], line1, line2;              vertices.push(                new THREE.Vector3(-1.65, 0.31, 0),                new THREE.Vector3(-1.65, h, 0),                new THREE.Vector3(-0.26, h, 0),                new THREE.Vector3(-0.26, 0.31, 0),                new THREE.Vector3(-1.65, 0.31, 0),              );              line1 = TH.createLineMesh(vertices, '#5CAEFD', 3, 2.5);              var vertices1 = [];              vertices1.push(                new THREE.Vector3(0.35, 0.31, 0),                new THREE.Vector3(0.77, h, 0),                new THREE.Vector3(1.04, h, 0),                new THREE.Vector3(1.45, 0.31, 0),                new THREE.Vector3(0.35, 0.31, 0),              );              line2 = TH.createLineMesh(vertices1, '#5CAEFD', 3, 2.5);              leftLine.add(line1, line2);              scene1.add(leftLine);            },            ImgL: function (src, callback) {              var img = new Image();              img.src = src;              img.onload = function () {                callback && callback();              }            },            //重置            resetWidget:function() {              thiz.start.color = '#000000';              $('.right-top div').html('<span></span>');              $('.right-top div span').css('background', '#bad6f2');              $('canvas').hide();              TH.ImgL('static/UI/subject.png', function () {                $('.leftMain').css({                  background: 'url(static/UI/subject.png)',                  backgroundRepeat: 'no-repeat',                  height: '400px',                  width: '100%',                  backgroundSize: '100% 100%',                  overflow: 'hidden'                });              });              scene1.remove(leftLine);              scene2.remove(dragPlane, cubeLine, triangle);              cubeLine = new THREE.Group();              triangle = new THREE.Group();              TH.createO();              thiz.yes = true;              thiz.radio_checked1 = '';              thiz.switch_checked4 = null;              thiz.radio_checked1O = '';              thiz.switch_checked4O1 = '';              thiz.switch_checked4O2 = '';              camera2.position.set(-6, 3, 9);              camera2.zoom = 1;              camera2.updateProjectionMatrix();              $('.mainD').show();            }          };        TH.setStyle();        TH.init1();        TH.init2();        TH.createO();        TH.animate();        renderer2.domElement.addEventListener('mousedown', TH.onDocumentMouseDown, false);        renderer2.domElement.addEventListener('mousemove', TH.onDocumentMouseMove, false);        renderer2.domElement.addEventListener('mouseup', TH.onDocumentMouseUp, false);        renderer2.domElement.addEventListener('touchstart', TH.onDocumentTouchStart, false);        renderer2.domElement.addEventListener('touchmove', TH.onDocumentTouchMove, false);        renderer2.domElement.addEventListener('touchend', TH.onDocumentTouchEnd, false);        if (isMob) {          $('.startC').on('touchstart', TH.btnClick);          $('.reset').on('touchstart', TH.resetWidget);        } else {          $('.startC').on('click', TH.btnClick);          $('.reset').on('click', TH.resetWidget);        }        window.onresize = () => {          TH.setStyle();        };      },    },  }</script><style>  * {    margin: 0;    padding: 0;  }  li {    list-style: none;  }  input, button {    outline: none;    -webkit-appearance: none;    border-radius: 0;  }  canvas {    outline: none;    display: none;  }  /*盒模型，padding尺寸不用再减去*/  *,  *:before,  *:after {    -moz-box-sizing: border-box;    -webkit-box-sizing: border-box;    box-sizing: border-box;    -webkit-tap-highlight-color: rgba(0, 0, 0, 0);    -webkit-tap-highlight-color: transparent;  }  html, body, #app {    width: 100%;    height: 100%;    overflow: hidden;    font-family: "PingFang SC", "Helvetica Neue", "Helvetica", "Arial", sans-serif;    background-color: #fff;    touch-action: none;    -ms-touch-action: none;    -webkit-touch-callout: none;    -webkit-user-select: none;    -khtml-user-select: none;    -moz-user-select: none;    -ms-user-select: none;    user-select: none;  }  #app {    -webkit-font-smoothing: antialiased;    -moz-osx-font-smoothing: grayscale;  }  .noselect {    -webkit-touch-callout: none; /* iOS Safari */    -webkit-user-select: none; /* Chrome/Safari/Opera */    -khtml-user-select: none; /* Konqueror */    -moz-user-select: none; /* Firefox */    -ms-user-select: none; /* Internet Explorer/Edge */    user-select: none; /* Non-prefixed version, currently not supported by any browser */  }  /*ui*/  .UI-camera {    width: 80px;    height: 80px;    cursor: pointer;  }  /*内容区*/  .container {    width: 100%;    height: calc(100% - 76px);    overflow: hidden;  }  canvas {    width: 100%;    height: 100%;  }  .insp-wrapper {    width: 100%;    height: 100%;  }  .left {    width: 372px;    height: 100%;    float: left;    margin: auto auto auto 24px;    position: relative;  }  .leftBody {    height: 460px;    position: absolute;    margin: auto;    left: 0;    right: 0;    top: 0;    bottom: 0  }  .leftMain {  }  #leftCanvas {    width: 300px;    height: 250px;    margin: 110px auto auto auto;  }  #main {    float: left;    margin-left: 20px;  }  .right {    float: right;    width: 100px;    height: 100%;    margin-right: 24px;    position: relative  }  .right-top {    width: 221px;    margin: auto auto 27px -121px;    height: 32px;  }  .step {    width: 72px;    margin-left: 1px;    height: 100%;    float: left;    text-align: center;    font-size: 12px;    color: #4D4D4D;  }  .step span {    width: 72px;    height: 6px;    background: #bad6f2;    border-radius: 100px;    display: block;    margin-bottom: 6px;  }</style>